{"changed":true,"filter":false,"title":"bowlling.rb","tooltip":"/lib/bowlling.rb","value":"# ボウリングのスコアを管理するクラス\nclass Bowling\n#インスタンスを生成する時に処理が実行される\ndef initialize\n    #スコアの合計\n    @total_score = 0\n    #scores = []\n    #一時保存用の配列\n    @temp = []\nend\n\n#スコアの合計を返す\ndef total_score\n    @total_score\nend\n\n#スコアを追加する\ndef add_score(pins)\n    #一時保存用のスコアに、倒したピンの数を追加する\n    @temp << pins\n    #2投分のデータが入っているか、1投目がストライクだったら、1フレーム分のスコアとして全体に追加する\n    if @temp.size == 2 || strike?(@temp)\n        @scores << @temp\n        @temp = []\n    end\nend\n\n#スコアの合計を計算する\ndef calc_score\n    @scores.each.with_index(1) do |score, index|\n    #最終フレーム以外でのストライクなら、スコアにボーナスを含めて合計する\n    if strike?(score) && not_last_frame?(index)\n    @total_score += calc_strike_bonus(index)\n    #最終フレーム以外でのスペアなら、スコアにボーナスを含めて合計する\n    elsif spare?(score) && not_last_frame?(index)\n    @total_score += calc_spare_bonus(index)\n    else\n    @total_score += score.inject(:+)\n    end\n    end\nend\nend\n\nprivate\n#スペアかどうか判定する\ndef spare?(score)\n    score.inject(:+) == 10\nend\n\n#ストライクかどうか判定する\ndef strike?(score)\n    score.first == 10\nend\n\n#最終フレームかどうか判定する\ndef not_last_frame?(index)\n    index < 10\nend\n\n#スペアボーナスを含んだ値でスコアを計算する\ndef calc_spare_bonus(index)\n    10 + @scores[index].first\nend\n    \nrequire \"bowling\"\n\ndescribe \"ボウリングのスコア計算\" do\n    #インスタンスの生成を共通化\n    before do\n        @game = Bolling.new\n    end\n    describe \"フレームごとの合計\" do\n        context \"全ての投球で1ピンずつ倒した場合\" do\n            it \"1フレーム目の合計が2になること\" do\n                add_many_scores(20,1)\n                #合計を計算\n                @game.calc_score\n                expect(@game.frame_scpre(1)).to eq 2\n        end\n    end\n    \n    context \"スペアを取った場合\" do\n        it \"スペアボーナスが加算されること\" do\n            #第一フレームで3点,7点のスペア\n            @game.add_score(3)\n            @game.add_score(7)\n            #第二フレームの一投目で4点\n            @game.add_score(4)\n            #以降は全てガター\n            add_many_scores(17,0)\n            #合計を計算\n            @game.calc_score\n            #期待する合計 ※()内はボーナス点\n            #3 + 7 + (4) = 14\n            expect(@game.frame_score(1)).to eq 14\n        end\n    end\n    \n    context \"ストライクを取った場合\" do\n        it \"ストライクボーナスが加算されること\" do\n            #第一フレームでストライク\n            @game.add_score(10)\n            #第二フレームで5点,4点\n            @game.add_score(5)\n            @game.add_score(4)\n            #以降は全てガター\n            add_many_scores(16, 0)\n            #合計を計算\n            @game.calc_score\n            #期待する合計 ※()内はボーナス点\n            #10 + (5) + (4) = 19\n            expect(@game.frame_score(1)).to eq 19\n        end\n    end\n    \n    describe \"全体の合計\" do\n        context \"全ての投球がガターだった場合\" do\n            it \"0になること\" do\n                add_many_scores(20, 0)\n                #合計を計算\n                @game.calc_score\n                expect(@game.total_score).to eq 0\n            end\n        end\n    end\n    context \"全ての投球で1ピンずつ倒した場合\" do\n        it \"20になること\" do\n        add_many_scores(20, 1)\n        #合計を計算\n        @game.calc_score\n        expect(@game.total_score).to eq 20\n        end\n    end\n    context \"スペアを取った場合\" do\n        it \"スペアボーナスが加算されること\" do\n            #第一フレームで3点,7点のスペア\n            @game.add_score(3)\n            @game.add_score(7)\n            #第二フレームの一投目で4点\n            @game.add_score(4)\n            #以降は全てガター\n            add_many_scores(17,0)\n            #合計を計算\n            @game.calc_score\n            #期待する合計 ※()内はボーナス点\n            #3 + 7 + 4 + (4) = 18\n            expect(@game.total_score).to eq 18\n        end\n    end\n    context \"フレーム違いでスペアになるようなスコアだった場合\" do\n        it \"スペアボーナスが加算されないこと\" do\n            #第一フレームで3点,5点\n            @game.add_score(3)\n            @game.add_score(5)\n            #第二フレームで5点,4点\n            @game.add_score(5)\n            @game.add_score(4)\n            #以降は全てガター\n            add_many_scores(16,0)\n            #合計を計算\n            @game.calc_score\n            #期待する合計\n            #3 + 5 + 5 + 4 = 17\n            expect(@game.total_score).to eq 17\n        end\n    end\n    context \"最終フレームでスペアを取った場合\" do\n        it \"スペアボーナスが加算されないこと\" do\n            #第一フレームで3点,7点のスペア\n            @game.add_score(3)\n            @game.add_score(7)\n            #第二フレームの一投目で4点\n            @game.add_score(4)\n            #15球は全てガター\n            add_many_scores(15,0)\n            #最終フレームで3点,7点のスペア\n            @game.add_score(3)\n            @game.add_score(7)\n            #合計を計算\n            @game.calc_score\n            #期待する合計 ※()内はボーナス点\n            #3 + 7 + 4 + (4) + 3 + 7 = 28\n            expect(@game.total_score).to eq 28\n        end\n    end\n    context \"ストライクを取った場合\" do\n        it \"ストライクボーナスが加算されること\" do\n            #第一フレームでストライク\n            @game.add_score(5)\n            @game.add_score(4)\n            #以降は全てガター\n            add_many_scores(16, 0)\n            #合計を計算\n            @game.calc_score\n            #期待する合計 ※()内はボーナス点\n            #10 + 5 + (5) + 4 + (4) = 28\n            expect(@game.total_score).to eq 28\n        end\n    end\n    context \"ダブルを取った場合\" do\n        it \"それぞれのストライクボーナスが加算されること\" do\n            #第一フレームでストライク\n            @game.add_score(10)\n            #第二フレームもストライク\n            @game.add_score(10)\n            #第三フレームで5点,4点\n            @game.add_score(5)\n            @game.add_score(4)\n            #以降は全てガター\n            add_many_scores(14,0)\n            #合計を計算\n            @game.calc_score\n            #期待する合計 ※()内はボーナス点\n            #10 + 10 + (10) + 5 + (5 + 5) + 4 + (4) = 53\n            expect(@game.total_score).to eq 53\n        end\n    end\n    context \"ターキーを取った場合\" do\n        it \"それぞれのストライクボーナスが加算されること\" do\n            #第一フレームでストライク\n            @game.add_score(10)\n            #第二フレームもストライク\n            @game.add_score(10)\n            #第三フレームもストライク\n            @game.add_score(10)\n            #第四フレームで5点,4点\n            @game.add_score(5)\n            @game.add_score(4)\n            #以降は全てガター\n            add_many_scores(12,0)\n            #合計を計算\n            @game.calc_score\n            #期待する合計 ※()内はボーナス点\n            #10 + 10 + (10) + 10 + (10 + 10) + 5 + (5 + 5) + 4 + (4) = 83\n            expect(@game.total_score).to eq 83\n        end\n    end\n    context \"最終フレームでストライクを取った場合\" do\n        it \"ストライクボーナスが加算されないこと\" do\n            #第一フレームでストライク\n            @game.add_score(10)\n            #第二フレームで5点,4点\n            @game.add_score(5)\n            @game.add_score(4)\n            #3～9フレームは全てガター\n            add_many_scores(14,0)\n            #最終フレームでストライク\n            @game.add_score(10)\n            #合計を計算\n            @game.calc_score\n            #期待する合計 ※()内はボーナス点\n            #10 + 5 + (5) + 4 + (4) + 10 = 38\n            expect(@game.total_score).to eq 38\n        end\n    end\nend\nend\nprivate\n# 複数回のスコア追加をまとめて実行する\ndef add_many_scores(count, pins)\n    count.times do\n        @game.add_score(pins)\n    end\nend\n    ","undoManager":{"mark":-101,"position":100,"stack":[[{"start":{"row":67,"column":13},"end":{"row":67,"column":18},"action":"insert","lines":["フレーム目"],"id":1645}],[{"start":{"row":67,"column":18},"end":{"row":67,"column":19},"action":"insert","lines":["の"],"id":1646},{"start":{"row":67,"column":19},"end":{"row":67,"column":22},"action":"insert","lines":["合計が"]}],[{"start":{"row":67,"column":22},"end":{"row":67,"column":23},"action":"insert","lines":["2"],"id":1647}],[{"start":{"row":67,"column":23},"end":{"row":67,"column":28},"action":"insert","lines":["になること"],"id":1648}],[{"start":{"row":67,"column":29},"end":{"row":67,"column":30},"action":"insert","lines":[" "],"id":1649},{"start":{"row":67,"column":30},"end":{"row":67,"column":31},"action":"insert","lines":["d"]},{"start":{"row":67,"column":31},"end":{"row":67,"column":32},"action":"insert","lines":["o"]}],[{"start":{"row":67,"column":32},"end":{"row":68,"column":0},"action":"insert","lines":["",""],"id":1650},{"start":{"row":68,"column":0},"end":{"row":68,"column":12}